const item = $input.first().json;
let raw = item.message ?? item.body ?? item.payload ?? item;
if (raw && typeof raw === 'object' && typeof raw.message === 'string') raw = raw.message;

let payload;
if (typeof raw === 'string') {
  try { payload = JSON.parse(raw); } catch(e) { throw new Error(`Payload no es JSON v√°lido: ${raw}`); }
} else { payload = raw; }

const area = payload.area;
const kwh  = Number(payload.kwh);
const timestamp = payload.timestamp || new Date().toISOString();

if (!area) throw new Error('Falta "area" en el payload.');
if (!Number.isFinite(kwh)) throw new Error(`"kwh" inv√°lido: ${payload.kwh}`);

return [{ json: { area, kwh: Number(kwh.toFixed(4)), timestamp, topic: item.topic ?? null, modo: payload.modo ?? 'normal' } }];

const ALPHA  = 0.2;
const WARMUP = Number($env?.WARMUP_READINGS ?? 50);

const parsedData   = $('Parsear MQTT').first().json;
const area         = parsedData.area;
const kwh          = parsedData.kwh;
const timestamp    = parsedData.timestamp;
const modo         = (parsedData.modo ?? 'normal').toLowerCase();
const ema_anterior = Number($input.first().json.ema_actual);
const contador_ant = Number($input.first().json.contador);
const es_primera   = contador_ant === 0;

// Detect if reading comes from an anomaly simulation mode
const esAnomalia = modo !== 'normal' && modo !== 'ok' && modo !== 'relay_off';
const noActualizarEma = esAnomalia || modo === 'relay_off';

// Only update EMA with normal readings to keep baseline stable
const ema_nuevo = es_primera ? kwh : (noActualizarEma ? ema_anterior : ALPHA * kwh + (1 - ALPHA) * ema_anterior);
const contador_nuevo = contador_ant + 1;

// For NORMAL areas: compare against ema_nuevo (original behavior)
// For ANOMALY areas: compare against ema_anterior (stable baseline)
const base_comparacion = es_primera ? kwh : (noActualizarEma ? ema_anterior : ema_nuevo);
const denom = Math.abs(base_comparacion) < 1e-9 ? 1e-9 : base_comparacion;
const desviacion = ((kwh - base_comparacion) / denom) * 100;
const enCalentamiento = contador_nuevo <= WARMUP;

let severidad = 'Normal';
if (!enCalentamiento && !es_primera) {
  if      (desviacion > 80) severidad = 'Critico';
  else if (desviacion > 35) severidad = 'Advertencia';
  // If simulator is in anomaly mode, force at least Advertencia
  if (esAnomalia && severidad === 'Normal') severidad = 'Advertencia';
}

return [{ json: { area, kwh: Number(kwh.toFixed(4)), ema: Number(ema_nuevo.toFixed(4)), desviacion: Number(desviacion.toFixed(2)), severidad, enCalentamiento, es_primera_lectura: es_primera, lectura_num: contador_nuevo, timestamp, modo } }];

