// Evaluar si la anomal√≠a requiere corte autom√°tico de emergencia
const item = $('Switch Severidad').item.json;
const sev = (item.severidad || '').toString().toLowerCase();
const desviacion = Number(item.desviacion) || 0;
const area = item.area || '';
const modo = (item.modo || 'normal').toLowerCase();

// No auto-cortar si es una simulaci√≥n de anomal√≠a (modo expl√≠cito del simulador)
const esSimulacion = modo !== 'normal' && modo !== 'ok' && modo !== 'relay_off';

// Reglas de auto-corte:
// 1. Severidad Cr√≠tica con desviaci√≥n > 150% (umbral alto)
// 2. NO es simulaci√≥n de anomal√≠a
// 3. √Årea sensible (sala_servidores queda excluida por seguridad)
const areasProtegidas = ['sala_servidores'];
const requiereCorte = !esSimulacion && sev.includes('crit') && desviacion > 150 && !areasProtegidas.includes(area);

return [{ json: { ...item, requiere_corte: requiereCorte, motivo: requiereCorte ? `Auto-corte: ${desviacion}% desviaci√≥n` : 'Solo alerta' } }];

// Procesar callbacks de acci√≥n operativa
const text = $('Normalizar Input').first().json.text || '';
const chat_id = $('Normalizar Input').first().json.chat_id;

let accion = '';
let area = '';
let tipo_feedback = '';

// Restore energy: restore_{area}
if (text.startsWith('restore_')) {
  area = text.replace('restore_', '');
  accion = 'restaurar_energia';
}
// Cut energy: cut_{area}
else if (text.startsWith('cut_')) {
  area = text.replace('cut_', '');
  accion = 'cortar_energia';
}
// Feedback: feedback_resuelto_{area} or feedback_falso_{area}
else if (text.startsWith('feedback_resuelto_')) {
  area = text.replace('feedback_resuelto_', '');
  tipo_feedback = 'resuelto';
}
else if (text.startsWith('feedback_falso_')) {
  area = text.replace('feedback_falso_', '');
  tipo_feedback = 'falso_positivo';
}

return [{ json: { chat_id, area, accion, tipo_feedback, original_text: text } }];
